from __future__ import print_function
# Variables starting with "_" are not accessible to the importing program

# See notes in visual/__init__.py

import imp as _imp
import sys as _sys

from visual_common.create_display import *

scene = display()

import atexit as _atexit

def _close_final():

    if len(_sys.argv[0]) > 0:
        try:
            while True: # at end of user program, wait for user to close the program
                rate(30)
        except:
            import traceback
            traceback.print_exc()
                
    _wx.Exit()

_atexit.register(_close_final)
