from visual import *
import wx

bchange = True
def change(evt):
    global bchange
    if bchange:
        solid.color = color.red
        cbutton.SetLabel('Make cyan')
    else:
        solid.color = color.cyan
        cbutton.SetLabel('Make red')
    bchange = not bchange

w = window(width=400, height=250)

p = w.panel
wx.TextCtrl(p, pos=(230,30), value='Type here:',
            size=(120,50), style=wx.TE_MULTILINE)
cbutton = wx.Button(p, label='Make red', pos=(240,100))
cbutton.Bind(wx.EVT_BUTTON, change)

disp = display(window=w, range=1.5,
            x=10, y=20, width=200, height=200)
solid = box(color=color.cyan)
disp.forward = (-1,-1,-1)

