#
# This is an atexit handler that's installed by vis/__init__.py and visual/__init__.py
# so that programs remain 'running' after they've finished executing the code in the
# program body. For visual this is important so that all the windows don't close
# before the user can interact.
#

import sys as _sys
import wx as _wx
from create_display import rate

def close_final():

    if len(_sys.argv[0]) > 0:
        while True: # at end of user program, wait for user to close the program
            rate(30)
                
    _wx.Exit()
